import {View, Text, StyleSheet, Image, useWindowDimensions} from 'react-native'
import PrimaryButton from '../components/ui/PrimaryButton';
import Title from '../components/ui/Title'
import Colors from '../constants/Colors';

function GameOverScreen({numberOfRounds, userNumber, startNewGameHandler}) {
    const {width, height} = useWindowDimensions();
    let imageSize = 300
    if(width< 380){
        imageSize = 150;
    }
    if(height <400){
        imageSize = 80;
    }
    const imageStyle = {
        width : imageSize,
        height: imageSize,
        borderRadius: imageSize/2,
    }
    return(
        <View style={styles.rootContainer}>
            <Title>Game Over!</Title>
            <View style={[styles.imageContainer, imageStyle]}>
               <Image style={styles.image} source={require('../assets/success.png')}/>
            </View>
            <Text style={styles.summaryText}>Your Phone needs <Text style={styles.highliteText}> {numberOfRounds} </Text> rounds to guess the number <Text style={styles.highliteText}> {userNumber} </Text></Text>
            <PrimaryButton onPress={startNewGameHandler}>Start New Game</PrimaryButton>
        </View>
    )
}

export default GameOverScreen;

// const deviceWidth = Dimensions.get('window').width

const styles = StyleSheet.create({
    rootContainer:{
        flex: 1,
        padding: 24,
        justifyContent: 'center',
        alignItems:'center'
    },
    imageContainer:{
        // width: deviceWidth < 380 ? 150 : 300,
        // height: deviceWidth < 380 ? 150 : 300,
        // borderRadius: deviceWidth < 380 ? 75 : 300,
        borderWidth: 3,
        borderColor: Colors.primary800,
        overflow: 'hidden',
        margin: 36
    },
    image: {
        width: '100%',
        height: '100%'
    },
    summaryText: {
        fontFamily: 'open-sans',
        fontSize: 24,
        textAlign: 'center',
        marginBottom: 24
    },
    highliteText: {
        fontFamily: 'open-sans-bold',
        color: Colors.primary500
    }
})
