import { useEffect, useState } from "react";
import {View, Text, StyleSheet, Alert, FlatList, Dimensions, useWindowDimensions } from "react-native";
import NumberContainer from "../components/game/NumberContainer";
import Card from "../components/ui/Card";
import InstructionText from "../components/ui/InstructionText";
import PrimaryButton from "../components/ui/PrimaryButton";
import Title from "../components/ui/Title";
import { Ionicons } from "@expo/vector-icons";
import Colors from "../constants/Colors";
import GuessLogItem from "../components/game/GuessLogItem";
const generateRandomBetween = (min, max, exclude) =>{
    const rndNum = Math.floor(Math.random() * (max - min)) + min;
    if(rndNum === exclude){
        return generateRandomBetween(min, max, exclude);
    }else {
        return rndNum;
    }
}
let minBoundary = 1, maxBoundary = 100;
const GameScreen = ({userNumber, onGameOver}) => {
    const initialGuess = generateRandomBetween(1, 100, userNumber);
    const [currentGuess, setCurrentGuess] = useState(initialGuess);
    const [guessRounds, setGuessRounds] = useState([initialGuess]);

    useEffect(() => {
        if(currentGuess === userNumber){
            onGameOver(guessRounds.length)
        }
    }, [currentGuess, userNumber, onGameOver])

    const nextGuessHandler = (direction) => {
        if(direction === 'lower' && currentGuess < userNumber 
        || direction === 'greater' && currentGuess > userNumber){
            Alert.alert("Don't lie!", "You know that this is wrong....!",[
                {text: 'Sorry!', style:'cancel'}
            ]);
            return;
        }
        if(direction === 'lower'){
            maxBoundary = currentGuess;
        } else {
            minBoundary = currentGuess + 1;
        }
        const newRndNumber = generateRandomBetween(minBoundary, maxBoundary, currentGuess);
        setCurrentGuess(newRndNumber);
        setGuessRounds(prevGuessRounds => [newRndNumber, ...prevGuessRounds])
    }

    let guessRoundLength =  guessRounds.length;

    const {width, height} = useWindowDimensions()

    let content;
    if(width < 500){
      content = (
        <>
          <NumberContainer>{currentGuess}</NumberContainer>
          <Card>
            <InstructionText style={styles.InstructionStyleObject}>
              Higher Or Lower
            </InstructionText>
            <View style={styles.buttonsContainer}>
              <View style={styles.buttonContainer}>
                <PrimaryButton onPress={nextGuessHandler.bind(this, "lower")}>
                  <Ionicons
                    name="md-remove"
                    size={24}
                    color={Colors.common.white}
                  />
                </PrimaryButton>
              </View>
              <View style={styles.buttonContainer}>
                <PrimaryButton onPress={nextGuessHandler.bind(this, "greater")}>
                  <Ionicons
                    name="md-add"
                    size={24}
                    color={Colors.common.white}
                  />
                </PrimaryButton>
              </View>
            </View>
          </Card>
        </>
      );
    }else {
      content = (
        <>
          <InstructionText style={styles.InstructionStyleObject}>
            Higher Or Lower
          </InstructionText>
          <View style={styles.buttonsContainerFormoreWidth}>
            <View style={styles.buttonContainer}>
              <PrimaryButton onPress={nextGuessHandler.bind(this, "lower")}>
                <Ionicons
                  name="md-remove"
                  size={24}
                  color={Colors.common.white}
                />
              </PrimaryButton>
            </View>
            <NumberContainer>{currentGuess}</NumberContainer>
            <View style={styles.buttonContainer}>
              <PrimaryButton onPress={nextGuessHandler.bind(this, "greater")}>
                <Ionicons name="md-add" size={24} color={Colors.common.white} />
              </PrimaryButton>
            </View>
          </View>

          <View style={styles.buttonsContainer}></View>
        </>
      );
    }

    return (
      <View style={styles.screen}>
        <Title>Opponents Guess</Title>
        {content}
        <View style={styles.listContainer}>
          {/* { guessRounds.map(item => <Text key={item}> {item}</Text>)} */}
          <FlatList
            data={guessRounds}
            renderItem={(itemData) => (
              <GuessLogItem
                roundNumber={guessRoundLength - itemData.index}
                guess={itemData.item}
              />
            )}
            keyExtractor={(item) => item}
          />
        </View>
      </View>
    );
}

export default GameScreen

const styles = StyleSheet.create({
    screen:{
        flex: 1,
        padding: 24,
        textAlign: 'center',
        alignItems:'center'
    },
    InstructionStyleObject:{
        marginBottom: 10
    },
    buttonsContainer: {
        flexDirection: 'row'
    },
    buttonContainer: {
        flex: 1
    },
    buttonsContainerFormoreWidth:{
      flexDirection: 'row',
      alignItems: 'center'
    },
    listContainer:{
      flex: 1,
      padding: 24
    }
})
